import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([]); // Store all tasks
  const [input, setInput] = useState(''); // User input

  // Add new task (if input is not empty)
  const addTodo = () => {
    if (input.trim() !== '') {
      setTodos([...todos, { text: input.trim(), completed: false }]);
      setInput('');
    }
  };

  // Mark a task as completed
  const markComplete = (index) => {
    const updatedTodos = [...todos];
    updatedTodos[index].completed = true;
    setTodos(updatedTodos);
  };

  // Delete a task by index
  const deleteTodo = (index) => {
    const updatedTodos = todos.filter((_, i) => i !== index);
    setTodos(updatedTodos);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: 'auto' }}>
      <h2>My To-Do List âœ…</h2>

      {/* Task input */}
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a task"
        style={{ padding: '8px', width: '70%' }}
      />

      {/* Add task button */}
      <button
        onClick={addTodo}
        style={{ padding: '8px', marginLeft: '5px' }}
        disabled={input.trim() === ''}
      >
        Add
      </button>

      {/* Display task list */}
      <ul style={{ listStyle: 'none', padding: 0, marginTop: '20px' }}>
        {todos.map((todo, index) => (
          <li key={index} style={{ marginBottom: '10px' }}>
            {/* Task text with styling */}
            <span
              style={{
                textDecoration: todo.completed ? 'line-through' : 'none',
                color: todo.completed ? 'gray' : 'black',
                marginRight: '10px'
              }}
            >
              {todo.text}
            </span>

            {/* Complete button (only if not yet completed) */}
            {!todo.completed && (
              <button onClick={() => markComplete(index)} style={{ padding: '4px 8px', marginRight: '5px' }}>
                Complete
              </button>
            )}

            {/* Delete button */}
            <button onClick={() => deleteTodo(index)} style={{ padding: '4px 8px', backgroundColor: 'red', color: 'white' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
